import { NgModule } from '@angular/core';
import { RouterModule } from '@angular/router';

@NgModule({
    imports: [
        RouterModule.forRoot([
            { path: '', redirectTo: 'screen/home', pathMatch: 'full' },
            { path: 'index.jsp', redirectTo: 'screen/home' },
            { path: 'screen/home', loadChildren: './features/home/home.module#HomeModule' },
            { path: 'screen/module1', loadChildren: './features/module.module#FeatureModule' },
            { path: 'screen/module2', loadChildren: './features/moduleNgrx.module#FeatureModule' },
        ])
    ],
    exports: [
        RouterModule
    ]
})
export class AppRoutingModule {

}
