import { Action } from '@ngrx/store';

import { SELECT_ENTITY, UPDATE_ENTITIES, ADD_ENTITY, CLEAR_ENTITIES, LOAD_DATA, LOAD_DATA_SUCCESS } from '../constants';

export class SelectEntityAction implements Action {
    type = SELECT_ENTITY;

    constructor(public payload: number) {}
}

export class UpdateEntitiesAction implements Action {
    type = UPDATE_ENTITIES;

    constructor(public payload: any) {}
}

export class AddEntityAction implements Action {
    type = ADD_ENTITY;

    constructor(public payload: any) {}
}

export class ClearEntitiesAction implements Action {
    type = CLEAR_ENTITIES;
}

export class LoadDataAction implements Action {
    type = LOAD_DATA;
}

export class LoadDataSuccessyAction implements Action {
    type = LOAD_DATA_SUCCESS;

    constructor(public payload: any) {}
}

export type Action = SelectEntityAction |
                        UpdateEntitiesAction |
                        AddEntityAction;
