import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs/Observable';

import { SelectEntityAction } from '../../actions/index';
import { getListEntities } from '../../reducers/index';

@Component({
    selector: '@module.name@-component2',
    templateUrl: './component2.component.html',
})
export class Component2Component implements OnInit {
    entities: Observable<any>;

    constructor(private store: Store<any>) {}

    ngOnInit() {
        this.entities = this.store.select(getListEntities);
    }

    selectEntity(id: number) {
        this.store.dispatch(new SelectEntityAction(id));
    }
}
