package eu.europa.ec.digit.eui.angular2starter.servlet;

import javax.servlet.*;
import javax.servlet.annotation.WebFilter;
import java.io.IOException;

/**
 * This filter forwards incoming Angular 2 router requests to the index page.
 * The responsibility for navigating is handed off to the Angular application itself which is loaded from the index page...
 **/
@WebFilter(value = {"/screen/*", "/index.html"}, filterName = "eu.europa.ec.digit.uxatec.eui.angular2.servlet.Angular2RouterFilter")
public class Angular2RouterFilter implements Filter {
    public void init(FilterConfig filterConfig) throws ServletException {
        // do nothing
    }

    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {
        servletRequest.getRequestDispatcher("/index.jsp").forward(servletRequest, servletResponse);
        servletResponse.flushBuffer();
    }

    public void destroy() {
        // do nothing
    }
}