package eu.europa.ec.digit.eui.web.api;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.ui.*;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.bind.support.*;

import java.util.*;

import eu.europa.ec.digit.eui.service.*;
import eu.europa.ec.digit.eui.common.dto.*;

@RestController
public class ApiController {

    @Autowired
    UserService userService;
    
    @RequestMapping("/users")
    public List<UserDTO> query() {
        UserDTO searchUserDTO = new UserDTO();
        return userService.findAll(searchUserDTO);
    }    

    @RequestMapping("/users/{id}")
    public UserDTO query(@PathVariable Integer id) {
        return userService.findOne(id);
    }

    @RequestMapping(value = "/users/{id}", method = RequestMethod.PUT)
    public UserDTO saveUser(@RequestBody UserDTO userDTO) {
        return userService.saveUser(userDTO);
    }    

    @RequestMapping(value = "/users/{id}", method = RequestMethod.DELETE)
    public UserDTO deleteUser(@PathVariable Integer id) {
        userService.deleteUser(id);
        return null;
    }  
}
