var app = angular.module('app', ['ui.router']);

app.config(function($stateProvider, $urlRouterProvider) {

    $urlRouterProvider.otherwise('/');

    $stateProvider
        .state('home', {
            url: '/',
            templateUrl: 'app/views/app-templates/app.html'
        })


        .state('style-guide', {
            url: '/style-guide',
            templateUrl: 'app/views/app-templates/style-guide/app.html'
        })
        .state('style-guide.ux-alert', {
            url: '/ux-alert',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-alert.html'
        })
        .state('style-guide.ux-atomic', {
            url: '/ux-atomic',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-atomic.html'
        })
        .state('style-guide.ux-badge', {
            url: '/ux-badge',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-badge.html'
        })
        .state('style-guide.ux-button', {
            url: '/ux-button',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-button.html'
        })
        .state('style-guide.ux-list-item', {
            url: '/ux-list-item',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-list-item.html'
        })
        .state('style-guide.ux-panel', {
            url: '/ux-panel',
            templateUrl: 'app/views/app-templates/style-guide/views/ux-panel.html'
        })


        .state('default', {
            url: '/default',
            templateUrl: 'app/views/app-templates/default/app.html'
        })
        .state('contract', {
            url: '/contract',
            templateUrl: 'app/views/app-templates/contract/app.html'
        })
        .state('sidebar', {
            url: '/sidebar',
            templateUrl: 'app/views/app-templates/sidebar/app.html'
        })
        .state('sidebar-overlay', {
            url: '/sidebar-overlay',
            templateUrl: 'app/views/app-templates/sidebar-overlay/app.html'
        })
        .state('sidebar-inner', {
            url: '/sidebar-inner',
            templateUrl: 'app/views/app-templates/sidebar-inner/app.html'
        })
        .state('landing-page', {
            url: '/landing-page',
            templateUrl: 'app/views/app-templates/landing-page/app.html'
        })


});

app.controller('appCtrl', function($scope, $location) {
    $scope.navigateToUrl = function(path) {
        $location.path(path);
    };

    $scope.toggleSidebar = function() {
        var $appWrapper = $('#app-wrapper'),
            $sidebar = $('.sidebar');

        if ($appWrapper.hasClass('sidebar-collapsible')) {

            if ($appWrapper.hasClass('sidebar-state-open')) {
                $appWrapper.removeClass('sidebar-state-open').addClass('sidebar-state-close');
            } else {
                $appWrapper.removeClass('sidebar-state-close').addClass('sidebar-state-open');
            }

            if ($sidebar.hasClass('state-open')) {
                $sidebar.removeClass('state-open').addClass('state-close');
            } else {
                $sidebar.removeClass('state-close').addClass('state-open');
            }
        }
    };

    $scope.toggleInnerSidebar = function() {
        var $appWrapper = $('#app-wrapper'),
            $sidebar = $('.sidebar');

        if ($appWrapper.hasClass('sidebar-state-open')) {
            $appWrapper.removeClass('sidebar-state-open').addClass('sidebar-state-close');
        } else {
            $appWrapper.removeClass('sidebar-state-close').addClass('sidebar-state-open');
        }

        if ($sidebar.hasClass('state-open')) {
            $sidebar.removeClass('state-open').addClass('state-close');
        } else {
            $sidebar.removeClass('state-close').addClass('state-open');
        }        
    }

    $scope.toggleOverlayPanel = function() {
        $('.overlay-panel').toggleClass('hidden');
    };
});

