import { NgModule } from '@angular/core';
import { RouterModule } from '@angular/router';

@NgModule({
    imports: [
        RouterModule.forRoot([
            { path: '', redirectTo: 'screen/home', pathMatch: 'full' },
            { path: 'index.html', redirectTo: 'screen/home' },
            { path: 'screen/home', loadChildren: './features/home/home.module#HomeModule' },
            { path: 'screen/buildings-overview', loadChildren: 'app/features/buildings/buildings.module#BuildingsModule' },
            { path: 'screen/module1', loadChildren: 'app/features/module1/module1.module#Module1Module' },
            { path: 'screen/module2', loadChildren: 'app/features/module2/module2.module#Module2Module' }
        ])
    ],
    exports: [
        RouterModule
    ]
})
export class AppRoutingModule {
}
