const execa = require('execa');
const path = require('path');

function compileSassFile(folder, filename) {
    return execa('node-sass', [
        folder + '/styles/' + filename + '.scss',
        folder + '/styles/' + filename + '.css',
        '--output-style',
        'compressed',
        '--source-map',
        true,
        '--source-map-contents'
    ]).catch((err) => {
        if (err.stderr !== 'No input file was found.\n') {
            console.log('[Node Sass Error]', err);
        }
        process.exit(1);
        return void 0;
    });
}

module.exports.compileSassFile = compileSassFile;
