'use strict';

const path = require('path');
const bootstrap = require('commitizen/dist/cli/git-cz').bootstrap;
const findNodeModules = require('find-node-modules');

const pathNodeModules = findNodeModules({ relative: false })[0];

bootstrap({
  cliPath: path.join(pathNodeModules, 'commitizen'),
  config: {
    "path": path.join(pathNodeModules, 'cz-conventional-changelog')
  }
});
